import { forwardRef, useRef, useImperativeHandle, useMemo } from "react";
import type { GridProps, BlockConfig, GridState } from "@/lib/grid-types";
import { cn } from "@/lib/utils";
import { GridProvider, useGridContext } from "@/components/grid/grid-provider";
import { useGridResize } from "@/hooks/use-grid-resize";
import { useGridKeyboard } from "@/hooks/use-grid-keyboard";
import { generateGridTemplate } from "@/lib/grid-calculations";
import { DividerOverlay } from "@/components/divider/divider-overlay";

/**
 * Grid API for imperative control
 */
export interface GridAPI {
  resizeBlock: (blockId: string, size: number) => void;
  collapseBlock: (blockId: string) => void;
  expandBlock: (blockId: string) => void;
  switchMode: (mode: string) => void;
  persistState: () => void;
  resetState: () => void;
  getState: () => GridState;
}

/**
 * Internal Grid component (wrapped by provider)
 */
const GridInternal = forwardRef<
  GridAPI,
  Omit<
    GridProps,
    | "defaultLayout"
    | "modes"
    | "persist"
    | "persistKey"
    | "onLayoutChange"
    | "onModeChange"
  >
>(({ children, className, "aria-label": ariaLabel }, ref) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const {
    state,
    resizeBlock,
    collapseBlock,
    expandBlock,
    switchMode,
    persistState,
    resetState,
  } = useGridContext();

  // Get resize state
  const isDragging = state.resize.isDragging;

  // Expose API through ref
  useImperativeHandle(
    ref,
    () => ({
      resizeBlock,
      collapseBlock,
      expandBlock,
      switchMode,
      persistState,
      resetState,
      getState: () => state,
    }),
    [
      resizeBlock,
      collapseBlock,
      expandBlock,
      switchMode,
      persistState,
      resetState,
      state,
    ]
  );

  // Get blocks as array sorted by order
  const blocks = useMemo(() => {
    const blockArray = Object.values(state.blocks);
    return blockArray.sort((a, b) => (a.order || 0) - (b.order || 0));
  }, [state.blocks]);

  // Get root block (parent_id is null)
  const rootBlock = useMemo(() => {
    return blocks.find((block) => !block.parentId);
  }, [blocks]);

  // Legacy resize support (keeping for backward compatibility)
  useGridResize({
    blocks,
    containerRef,
    onSizeChange: resizeBlock,
    direction: rootBlock?.direction || "row",
  });

  // Set up keyboard navigation
  useGridKeyboard({
    enabled: true,
    blocks,
    containerRef,
    onResizeBlock: (blockId, delta) => {
      const block = state.blocks[blockId];
      if (block) {
        const currentSize = block.defaultSize || 0;
        const newSize = Math.max(0, currentSize + delta);
        resizeBlock(blockId, newSize);
      }
    },
    onCollapseBlock: collapseBlock,
    onExpandBlock: expandBlock,
  });

  // Note: Dividers are now handled by DividerOverlay as absolutely positioned overlays

  // Generate CSS styles for the grid
  const { gridStyles, cssVariables, modeStyles } = useMemo(() => {
    if (!rootBlock) {
      return { gridStyles: "", cssVariables: "", modeStyles: "" };
    }

    // Get blocks grouped by parent
    const blocksByParent = blocks.reduce((acc, block) => {
      // Skip the root block itself when grouping by parent
      if (block.id === rootBlock.id) return acc;

      const parentId = block.parentId || rootBlock.id;
      if (!acc[parentId]) acc[parentId] = [];
      acc[parentId].push(block);
      return acc;
    }, {} as Record<string, BlockConfig[]>);

    // Generate CSS variables for block sizes (scoped by grid ID to prevent collisions)
    const variables = blocks
      .filter((block) => block.defaultSize !== undefined)
      .map((block) => {
        const sizeValue =
          block.sizeUnit === "px"
            ? `${block.defaultSize}px`
            : `${block.defaultSize}fr`;
        return `--${rootBlock.id}-${block.id}-size: ${sizeValue};`;
      })
      .join("\n  ");

    // Mode-specific styles removed - will be re-added when modes are properly designed
    const generateModeStyles = (): string => {
      return "";
    };

    // Generate dynamic grid styles based on block tree structure
    const generateGroupStyles = (
      parentId: string,
      visited: Set<string> = new Set()
    ): string => {
      // Prevent infinite recursion
      if (visited.has(parentId)) {
        console.warn(`Circular reference detected for parent: ${parentId}`);
        return "";
      }

      // Create a new set for this branch to avoid cross-contamination
      const newVisited = new Set(visited);
      newVisited.add(parentId);

      const childBlocks = blocksByParent[parentId] || [];
      if (childBlocks.length === 0) return "";

      // Sort by order
      const sortedBlocks = [...childBlocks].sort(
        (a, b) => (a.order || 0) - (b.order || 0)
      );

      // Generate grid template for this group
      const parentBlock = blocks.find((b) => b.id === parentId) || rootBlock;
      const direction = parentBlock?.direction || "row";

      // Generate grid template from blocks (dividers are overlays, not part of the grid)
      const blocksForTemplate = sortedBlocks.map((block) => ({
        id: block.id,
        sizeUnit: block.sizeUnit || "fr",
        size: block.defaultSize || 1,
        dividerPosition: "none" as const, // Dividers are overlays, not in grid template
        dividerSize: 0, // Not used since dividers are overlays
      }));

      const template = generateGridTemplate(blocksForTemplate, rootBlock.id);

      const templateProperty =
        direction === "column" ? "grid-template-rows" : "grid-template-columns";

      // Simple selector - just target the block group by ID and type
      // Grid-scoped to prevent collisions across multiple grids
      const selector = `[data-block-id="${parentId}"]`;

      let groupStyle = `
${selector} {
  display: grid;
  ${templateProperty}: ${template};
  ${
    direction === "column"
      ? "grid-template-columns: 1fr;"
      : "grid-template-rows: 1fr;"
  }
}`;

      // Generate styles for child groups recursively
      for (const childBlock of sortedBlocks) {
        if (childBlock.type === "group") {
          groupStyle += generateGroupStyles(childBlock.id, newVisited);
        }
      }

      return groupStyle;
    };

    const dynamicStyles = generateGroupStyles(rootBlock.id);
    const modeDynamicStyles = generateModeStyles();

    return {
      cssVariables: `:root {\n  ${variables}\n}`,
      gridStyles: dynamicStyles,
      modeStyles: modeDynamicStyles,
    };
  }, [blocks, rootBlock]);

  if (!rootBlock) {
    console.warn("No root block found in grid configuration");
    return null;
  }

  return (
    <>
      {/* Inject styles */}
      <style type="text/css">
        {cssVariables}
        {gridStyles}
        {modeStyles}
      </style>

      <div
        ref={containerRef}
        className={cn(
          "group relative overflow-hidden",
          isDragging &&
            "select-none cursor-grabbing pretty-poly-grid--dragging",
          className
        )}
        data-grid-id={rootBlock.id}
        data-block-id={rootBlock.id}
        data-block-type={rootBlock.type}
        data-active-mode={state.activeMode}
        aria-label={ariaLabel || "Resizable grid layout"}
        role="application"
        style={{ isolation: "isolate" }}
      >
        {children}

        {/* Divider overlay - only in grid/desktop mode */}
        {(state.activeMode === "grid" || state.activeMode === "desktop") && (
          <DividerOverlay />
        )}
      </div>
    </>
  );
});

GridInternal.displayName = "GridInternal";

/**
 * Main Grid component with provider wrapper
 */
export const Grid = forwardRef<GridAPI, GridProps>(
  (
    {
      children,
      defaultLayout = [],
      modes,
      persist = false,
      persistKey,
      onLayoutChange,
      onModeChange,
      className,
      dividers = "manual",
      dividerConfig,
      "aria-label": ariaLabel,
    },
    ref
  ) => {
    // Find root block ID for gridId
    const rootBlock = defaultLayout.find((block) => !block.parentId);
    const gridId = rootBlock?.id || "root";

    return (
      <GridProvider
        blocks={defaultLayout}
        modes={modes}
        gridId={gridId}
        persist={persist}
        persistKey={persistKey}
        onLayoutChange={onLayoutChange}
        onModeChange={onModeChange}
      >
        <GridInternal
          ref={ref}
          className={className}
          dividers={dividers}
          dividerConfig={dividerConfig}
          aria-label={ariaLabel}
        >
          {children}
        </GridInternal>
      </GridProvider>
    );
  }
);

Grid.displayName = "Grid";

import React, {
  createContext,
  useContext,
  useReducer,
  useEffect,
  useMemo,
} from "react";
import type {
  GridState,
  GridAction,
  GridContextValue,
  BlockConfig,
  ResponsiveModes,
  ViewportInfo,
} from "@/lib/grid-types";
import { useGridMode } from "@/hooks/use-grid-mode";
import { useGridPersistence } from "@/hooks/use-grid-persistence";
import { useGridResizeOperations } from "@/hooks/use-grid-resize-operations";

/**
 * Grid state reducer
 */
function gridStateReducer(state: GridState, action: GridAction): GridState {
  switch (action.type) {
    case "RESIZE_BLOCK":
      const block = state.blocks[action.payload.blockId];
      if (!block) return state;

      return {
        ...state,
        blocks: {
          ...state.blocks,
          [action.payload.blockId]: {
            ...block,
            defaultSize: action.payload.size,
            size: action.payload.size,
          },
        },
      };

    case "COLLAPSE_BLOCK":
      const collapseBlock = state.blocks[action.payload.blockId];
      if (!collapseBlock) return state;

      const collapseSize = collapseBlock.collapseTo || 0;
      return {
        ...state,
        blocks: {
          ...state.blocks,
          [action.payload.blockId]: {
            ...collapseBlock,
            // Preserve original size for expand
            originalDefaultSize:
              collapseBlock.originalDefaultSize || collapseBlock.defaultSize,
            defaultSize: collapseSize,
            size: collapseSize,
          },
        },
      };

    case "EXPAND_BLOCK":
      const expandBlock = state.blocks[action.payload.blockId];
      if (!expandBlock) return state;

      // Restore to the original default size defined in configuration
      const originalSize =
        expandBlock.originalDefaultSize || expandBlock.defaultSize || 100;
      return {
        ...state,
        blocks: {
          ...state.blocks,
          [action.payload.blockId]: {
            ...expandBlock,
            defaultSize: originalSize,
            size: originalSize,
          },
        },
      };

    case "SET_ACTIVE_DIVIDER":
      return {
        ...state,
        activeDivider: action.payload.dividerId,
      };

    case "SWITCH_MODE":
      return {
        ...state,
        activeMode: action.payload.mode,
      };

    case "UPDATE_VIEWPORT":
      return {
        ...state,
        viewport: action.payload.viewport,
      };

    case "LOAD_STATE":
      return {
        ...state,
        ...action.payload.state,
        // Always preserve current viewport
        viewport: state.viewport,
      };

    case "RESET_STATE":
      // Reset block sizes to their original defaults
      const resetBlocks = Object.fromEntries(
        Object.entries(state.blocks).map(([id, block]) => [
          id,
          {
            ...block,
            size: block.defaultSize,
            // Reset to original defaultSize stored somewhere, or current defaultSize
          },
        ])
      );
      return {
        ...state,
        blocks: resetBlocks,
        activeDivider: undefined,
        resize: {
          isDragging: false,
          startPosition: { x: 0, y: 0 },
          initialSize: 0,
        },
      };

    case "START_RESIZE":
      return {
        ...state,
        resize: {
          isDragging: true,
          activeBlockId: action.payload.blockId,
          activeDividerId: action.payload.dividerId,
          startPosition: action.payload.startPosition,
          initialSize: action.payload.initialSize,
          initialAdjacentBlockId: action.payload.initialAdjacentBlockId,
          initialAdjacentSize: action.payload.initialAdjacentSize,
        },
      };

    case "UPDATE_RESIZE":
      return {
        ...state,
        resize: {
          ...state.resize,
          // The resize calculation happens in the resize handler, not the reducer
        },
      };

    case "END_RESIZE":
      return {
        ...state,
        resize: {
          isDragging: false,
          startPosition: { x: 0, y: 0 },
          initialSize: 0,
        },
      };

    default:
      return state;
  }
}

/**
 * Create initial grid state from block configurations
 */
function createInitialState(
  blocks: BlockConfig[],
  viewport: ViewportInfo,
  activeMode: string
): GridState {
  const blocksMap = blocks.reduce((acc, block) => {
    acc[block.id] = {
      ...block,
      size: block.defaultSize,
      originalDefaultSize: block.defaultSize, // Store original size for expand functionality
    };
    return acc;
  }, {} as Record<string, BlockConfig>);

  return {
    blocks: blocksMap,
    activeMode,
    viewport,
    resize: {
      isDragging: false,
      startPosition: { x: 0, y: 0 },
      initialSize: 0,
    },
  };
}

// Context
const GridContext = createContext<GridContextValue | null>(null);

export interface GridProviderProps {
  children: React.ReactNode;
  blocks: BlockConfig[];
  modes?: ResponsiveModes;
  gridId?: string;
  persist?:
    | boolean
    | "localStorage"
    | "sessionStorage"
    | ((state: GridState) => void);
  persistKey?: string;
  onModeChange?: (newMode: string, previousMode: string) => void;
  onLayoutChange?: (blocks: BlockConfig[]) => void;
}

/**
 * Grid context provider component
 */
export function GridProvider({
  children,
  blocks,
  modes,
  gridId = "default",
  persist = false,
  persistKey,
  onModeChange,
  onLayoutChange,
}: GridProviderProps) {
  // Mode management
  const { activeMode, viewport, setMode: setModeInternal } = useGridMode(modes);

  // Initialize state
  const [state, dispatch] = useReducer(
    gridStateReducer,
    createInitialState(blocks, viewport, activeMode)
  );

  // Update viewport in state when it changes
  useEffect(() => {
    dispatch({
      type: "UPDATE_VIEWPORT",
      payload: { viewport },
    });
  }, [viewport]);

  // Update active mode in state when it changes
  useEffect(() => {
    const previousMode = state.activeMode;
    if (activeMode !== previousMode) {
      dispatch({
        type: "SWITCH_MODE",
        payload: { mode: activeMode },
      });
      onModeChange?.(activeMode, previousMode);
    }
  }, [activeMode, state.activeMode, onModeChange]);

  // Persistence
  const { saveState, clearState } = useGridPersistence({
    gridId: persistKey || gridId,
    enabled: persist,
    state,
    onStateLoad: (loadedState) => {
      dispatch({ type: "LOAD_STATE", payload: { state: loadedState } });
    },
    autoSave: true,
  });

  // Resize operations (extracted to custom hook)
  const { startResize, updateResize, endResize } = useGridResizeOperations(state, dispatch);

  // Create context value
  const contextValue: GridContextValue = useMemo(
    () => ({
      gridId,
      state: {
        ...state,
        activeMode,
        viewport,
      },
      dispatch,

      // Grid operations
      resizeBlock: (blockId: string, size: number) => {
        dispatch({
          type: "RESIZE_BLOCK",
          payload: { blockId, size },
        });
      },

      collapseBlock: (blockId: string) => {
        dispatch({
          type: "COLLAPSE_BLOCK",
          payload: { blockId },
        });
      },

      expandBlock: (blockId: string) => {
        dispatch({
          type: "EXPAND_BLOCK",
          payload: { blockId },
        });
      },

      switchMode: (mode: string) => {
        setModeInternal(mode);
      },

      // Resize operations (using extracted hook)
      startResize,
      updateResize,
      endResize,

      // Persistence
      persistState: () => saveState(state),
      resetState: () => {
        dispatch({ type: "RESET_STATE" });
        clearState();
      },
    }),
    [gridId, state, activeMode, viewport, saveState, clearState, setModeInternal, startResize, updateResize, endResize]
  );

  // Notify parent of layout changes
  useEffect(() => {
    if (onLayoutChange) {
      const blockConfigs = Object.values(state.blocks);
      onLayoutChange(blockConfigs);
    }
  }, [state.blocks, onLayoutChange]);

  return (
    <GridContext.Provider value={contextValue}>{children}</GridContext.Provider>
  );
}

/**
 * Hook to access grid context
 */
// eslint-disable-next-line react-refresh/only-export-components
export function useGridContext(): GridContextValue {
  const context = useContext(GridContext);
  if (!context) {
    throw new Error("useGridContext must be used within a GridProvider");
  }
  return context;
}

/**
 * Hook to access grid state
 */
// eslint-disable-next-line react-refresh/only-export-components
export function useGridState() {
  const { state } = useGridContext();
  return state;
}

/**
 * Hook to access grid actions
 */
// eslint-disable-next-line react-refresh/only-export-components
export function useGridActions() {
  const {
    resizeBlock,
    collapseBlock,
    expandBlock,
    switchMode,
    persistState,
    resetState,
  } = useGridContext();
  return {
    resizeBlock,
    collapseBlock,
    expandBlock,
    switchMode,
    persistState,
    resetState,
  };
}

/**
 * Hook to access resize operations
 */
// eslint-disable-next-line react-refresh/only-export-components
export function useGridResize() {
  const { startResize, updateResize, endResize, state } = useGridContext();
  return {
    startResize,
    updateResize,
    endResize,
    isDragging: state.resize.isDragging,
    activeBlockId: state.resize.activeBlockId,
    activeDividerId: state.resize.activeDividerId,
  };
}

import { forwardRef, useMemo } from 'react'
import type { BlockProps } from "@/lib/grid-types"
import { cn } from "@/lib/utils"
import { useGridContext, useGridState, useGridActions } from "@/components/grid/grid-provider"
import { useGridMode } from "@/hooks/use-grid-mode"
import { BlockContent } from "@/components/grid/block-content"
import { BlockHeader } from "@/components/grid/block-header"
import { BlockFooter } from "@/components/grid/block-footer"
import { BlockToolbar } from "@/components/grid/block-toolbar"
import { BlockTabs } from "@/components/grid/block-tabs"
import { BlockSidebar } from "@/components/grid/block-sidebar"
import { BlockSidebarItem } from "@/components/grid/block-sidebar-item"
import { BlockSidebarSpacer } from "@/components/grid/block-sidebar-spacer"
import { BlockLayout } from "@/components/grid/block-layout"

/**
 * Block component for grid layouts
 */
export const Block = forwardRef<HTMLDivElement, BlockProps>(
  ({
    id,
    type = 'block',
    direction = 'row',
    children,
    className,
    divider,
    noDivider,
    'aria-label': ariaLabel
  }, ref) => {
    const { gridId } = useGridContext()
    const state = useGridState()
    const { collapseBlock, expandBlock } = useGridActions()
    const { supportsFeature } = useGridMode()

    // Get block configuration from state
    const blockConfig = state?.blocks[id]

    // Calculate collapsed state
    const isCollapsed = useMemo(() => {
      if (blockConfig?.collapsible && blockConfig.collapseAt) {
        const currentSize = blockConfig.size ?? blockConfig.defaultSize ?? 0
        return currentSize <= blockConfig.collapseAt
      }
      return false
    }, [blockConfig])

    // Handle double-click for collapse/expand
    const handleDoubleClick = () => {
      if (blockConfig?.collapsible && blockConfig.collapseAt) {
        const currentSize = blockConfig.size ?? blockConfig.defaultSize ?? 0
        const isCollapsed = currentSize <= blockConfig.collapseAt
        if (isCollapsed) {
          expandBlock(id)
        } else {
          collapseBlock(id)
        }
      }
    }

    return (
      <div
        ref={ref}
        className={cn(
          // Base styles - simple grid item that fills its space
          'relative',
          'w-full h-full',
          'overflow-hidden',
          'transition-opacity duration-150',
          isCollapsed && 'opacity-70',
          className
        )}
        data-grid-id={gridId}
        data-block-id={id}
        data-block-type={type}
        data-block-direction={direction}
        data-block-size-default={blockConfig?.defaultSize}
        data-block-size-unit={blockConfig?.sizeUnit}
        data-block-size-min={blockConfig?.minSize}
        data-block-size-max={blockConfig?.maxSize}
        data-block-collapse-at={blockConfig?.collapseAt}
        data-block-collapse-to={blockConfig?.collapseTo}
        data-block-divider-position={blockConfig?.dividerPosition}
        data-block-divider-size={blockConfig?.dividerSize}
        data-block-divider={divider !== undefined ? JSON.stringify(divider) : undefined}
        data-block-no-divider={noDivider}
        aria-label={ariaLabel}
        role={type === 'group' ? 'group' : undefined}
        tabIndex={supportsFeature('resizing') ? 0 : undefined}
        onDoubleClick={supportsFeature('collapse') ? handleDoubleClick : undefined}
      >
        {children}
      </div>
    )
  }
)

Block.displayName = 'Block'

/**
 * Block.Group component for nested layouts
 */
export const BlockGroup = forwardRef<HTMLDivElement, BlockProps>(
  (props, ref) => {
    return <Block ref={ref} {...props} type="group" />
  }
)

BlockGroup.displayName = 'Block.Group'

// Add all sub-components as properties of Block
Object.assign(Block, {
  Group: BlockGroup,
  Layout: BlockLayout,
  Header: BlockHeader,
  Content: BlockContent,
  Footer: BlockFooter,
  Toolbar: BlockToolbar,
  Tabs: BlockTabs,
  Sidebar: BlockSidebar
})

// Add nested sidebar components
Object.assign(BlockSidebar, {
  Item: BlockSidebarItem,
  Spacer: BlockSidebarSpacer
})

import { forwardRef } from 'react'
import { cn } from "@/lib/utils"

export type ScrollMode = 'vertical' | 'horizontal' | 'both' | 'none'

export interface BlockContentProps {
  scrollMode?: ScrollMode
  className?: string
  children: React.ReactNode
  'aria-label'?: string
}

/**
 * BlockContent component for main scrollable content areas
 * Handles different scrolling behaviors and fills remaining space in block
 */
export const BlockContent = forwardRef<HTMLDivElement, BlockContentProps>(
  ({ scrollMode = 'vertical', className, children, 'aria-label': ariaLabel }, ref) => {
    const scrollClasses = {
      vertical: 'overflow-y-auto overflow-x-hidden',
      horizontal: 'overflow-x-auto overflow-y-hidden',
      both: 'overflow-auto',
      none: 'overflow-hidden'
    }

    return (
      <div
        ref={ref}
        className={cn(
          'pretty-poly-block-content',
          'flex-1', // Fill remaining space
          'min-h-0', // Allow flex shrinking
          scrollClasses[scrollMode],
          className
        )}
        data-scroll-mode={scrollMode}
        aria-label={ariaLabel}
        role="main"
      >
        {children}
      </div>
    )
  }
)

BlockContent.displayName = 'Block.Content'

import { forwardRef } from 'react'
import { cn } from "@/lib/utils"

export type HeaderPosition = 'top' | 'bottom'

export interface BlockHeaderProps {
  position?: HeaderPosition
  className?: string
  children: React.ReactNode
  'aria-label'?: string
}

/**
 * BlockHeader component for fixed header/toolbar areas
 * Does not scroll with content, stays in fixed position
 */
export const BlockHeader = forwardRef<HTMLDivElement, BlockHeaderProps>(
  ({ position = 'top', className, children, 'aria-label': ariaLabel }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(
          'pretty-poly-block-header',
          'flex-shrink-0', // Don't shrink
          'flex flex-row items-center', // Default to horizontal layout
          'min-h-0', // Allow content to determine height
          position === 'bottom' && 'order-last', // Move to bottom if specified
          className
        )}
        data-header-position={position}
        aria-label={ariaLabel}
        role="banner"
      >
        {children}
      </div>
    )
  }
)

BlockHeader.displayName = 'Block.Header'

import { forwardRef } from 'react'
import { cn } from "@/lib/utils"

export interface BlockFooterProps {
  className?: string
  children: React.ReactNode
  'aria-label'?: string
}

/**
 * BlockFooter component for fixed footer/status bar areas
 * Does not scroll with content, stays at bottom of block
 */
export const BlockFooter = forwardRef<HTMLDivElement, BlockFooterProps>(
  ({ className, children, 'aria-label': ariaLabel }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(
          'pretty-poly-block-footer',
          'flex-shrink-0', // Don't shrink
          'flex flex-row items-center', // Default to horizontal layout
          'min-h-0', // Allow content to determine height
          'order-last', // Always at bottom
          className
        )}
        aria-label={ariaLabel}
        role="contentinfo"
      >
        {children}
      </div>
    )
  }
)

BlockFooter.displayName = 'Block.Footer'

import { forwardRef } from 'react'
