import React, { useRef, useCallback, useState, useLayoutEffect, useEffect } from 'react'
import { cn } from "@/lib/utils"
import { useGridState, useGridResize } from "@/components/grid/grid-provider"

interface DividerProps {
  targetId: string
  position: 'start' | 'end'
  direction: 'vertical' | 'horizontal'
  size?: number
  className?: string
  'aria-label'?: string
}

/**
 * Absolutely positioned divider overlay component
 * Positioned using getBoundingClientRect for accuracy
 */
export const Divider: React.FC<DividerProps> = ({
  targetId,
  position,
  direction,
  size = 4, // Default hover zone size (like VS Code)
  className,
  'aria-label': ariaLabel
}) => {
  const dividerRef = useRef<HTMLDivElement>(null)
  const state = useGridState()
  const { startResize, isDragging, activeDividerId } = useGridResize()

  const [dividerPosition, setDividerPosition] = useState({
    left: 0,
    top: 0,
    width: 0,
    height: 0
  })

  const targetBlock = state.blocks[targetId]
  const isVertical = direction === 'vertical'
  const dividerId = `${targetId}-${position}-divider`
  const isActive = isDragging && activeDividerId === dividerId

  /**
   * Calculate and update divider position based on block boundaries
   */
  const updatePosition = useCallback(() => {
    // Find the grid container (has data-grid-id)
    const gridContainer = document.querySelector('[data-grid-id]') as HTMLElement
    const targetElement = document.querySelector(`[data-block-id="${targetId}"]`) as HTMLElement

    if (!gridContainer || !targetElement) return

    const containerRect = gridContainer.getBoundingClientRect()
    const blockRect = targetElement.getBoundingClientRect()

    // Find parent block to constrain divider dimensions for nested blocks
    const parentId = targetBlock?.parentId
    const parentElement = parentId
      ? document.querySelector(`[data-block-id="${parentId}"]`) as HTMLElement
      : gridContainer

    if (!parentElement) return

    const parentRect = parentElement.getBoundingClientRect()

    if (isVertical) {
      // Vertical divider - positioned on left or right edge of block
      const edgePosition = position === 'start'
        ? blockRect.left - containerRect.left
        : blockRect.right - containerRect.left

      setDividerPosition({
        left: edgePosition - (size / 2), // Center on edge
        top: parentRect.top - containerRect.top, // Position relative to parent
        width: size,
        height: parentRect.height // Use parent height to constrain divider
      })
    } else {
      // Horizontal divider - positioned on top or bottom edge of block
      const edgePosition = position === 'start'
        ? blockRect.top - containerRect.top
        : blockRect.bottom - containerRect.top

      setDividerPosition({
        left: parentRect.left - containerRect.left, // Position relative to parent
        top: edgePosition - (size / 2), // Center on edge
        width: parentRect.width, // Use parent width to constrain divider
        height: size
      })
    }
  }, [targetId, position, isVertical, size, targetBlock?.parentId])

  /**
   * Set up ResizeObserver to track block and container size changes
   */
  useLayoutEffect(() => {
    const gridContainer = document.querySelector('[data-grid-id]') as HTMLElement
    const targetElement = document.querySelector(`[data-block-id="${targetId}"]`) as HTMLElement

    if (!gridContainer || !targetElement) return

    // Initial position
    updatePosition()

    // Watch for size changes
    const resizeObserver = new ResizeObserver(() => {
      updatePosition()
    })

    resizeObserver.observe(gridContainer)
    resizeObserver.observe(targetElement)

    // Also observe parent element if it exists (for nested blocks)
    const parentId = targetBlock?.parentId
    const parentElement = parentId
      ? document.querySelector(`[data-block-id="${parentId}"]`) as HTMLElement
      : null

    if (parentElement) {
      resizeObserver.observe(parentElement)
    }

    return () => {
      resizeObserver.disconnect()
    }
  }, [targetId, updatePosition, targetBlock?.parentId])

  /**
   * Also update position when blocks change (resize operations)
   */
  useEffect(() => {
    updatePosition()
  }, [state.blocks, updatePosition])

  /**
   * Handle resize start
   */
  const handlePointerDown = useCallback((event: React.MouseEvent | React.TouchEvent) => {
    event.preventDefault()
    startResize(targetId, dividerId, event)
  }, [targetId, dividerId, startResize])

  if (!targetBlock) {
    return null
  }

  return (
    <div
      ref={dividerRef}
      className={cn(
        'pretty-poly-divider',
        'absolute',
        'bg-transparent', // Invisible by default
        'transition-colors duration-100',
        'hover:bg-[var(--divider-hover-color,rgba(59,130,246,0.5))]',
        isActive && 'bg-[var(--divider-active-color,rgba(59,130,246,0.7))]',
        isVertical ? 'cursor-col-resize' : 'cursor-row-resize',
        className
      )}
      style={{
        left: `${dividerPosition.left}px`,
        top: `${dividerPosition.top}px`,
        width: `${dividerPosition.width}px`,
        height: `${dividerPosition.height}px`,
        pointerEvents: 'auto', // Re-enable pointer events (parent has pointer-events: none)
        zIndex: 10
      }}
      data-divider-id={dividerId}
      data-target-block={targetId}
      data-divider-position={position}
      data-divider-direction={direction}
      role="separator"
      aria-label={ariaLabel || `Resize ${targetId}`}
      aria-valuenow={targetBlock?.defaultSize}
      aria-valuemin={targetBlock?.minSize}
      aria-valuemax={targetBlock?.maxSize}
      tabIndex={0}
      onMouseDown={handlePointerDown}
      onTouchStart={handlePointerDown}
    />
  )
}

Divider.displayName = 'Divider'

import React, { useMemo } from 'react'
import { useGridState } from "@/components/grid/grid-provider"
import { Divider } from "@/components/divider/divider"
import { autoDetectDividerPosition } from "@/lib/grid-divider-auto-detection"
import type { BlockConfig } from "@/lib/grid-types"

interface DividerInfo {
  id: string
  targetBlockId: string
  position: 'start' | 'end'
  direction: 'vertical' | 'horizontal'
}

/**
 * Auto-generates dividers based on block configuration
 * Uses smart position detection to determine which block should be resized
 */
function generateDividers(blocks: Record<string, BlockConfig>): DividerInfo[] {
  const dividers: DividerInfo[] = []

  // Find all group blocks
  const groups = Object.values(blocks).filter(block => block.type === 'group')

  groups.forEach(group => {
    // Get children of this group, sorted by order
    const children = Object.values(blocks)
      .filter(block => block.parentId === group.id)
      .sort((a, b) => (a.order || 0) - (b.order || 0))

    if (children.length === 0) return

    const isHorizontal = group.direction === 'row'
    const dividerDirection: 'vertical' | 'horizontal' = isHorizontal ? 'vertical' : 'horizontal'

    // Create divider between each pair of children
    children.forEach((child, index) => {
      if (index === 0) return // No divider before first child

      const previousChild = children[index - 1]

      // Use smart detection to determine which block should be the target
      const { targetId, position } = autoDetectDividerPosition(previousChild, child)

      dividers.push({
        id: `divider-${previousChild.id}-${child.id}`,
        targetBlockId: targetId,
        position: position as 'start' | 'end',
        direction: dividerDirection
      })
    })
  })

  return dividers
}

/**
 * Overlay container that manages all dividers as absolutely positioned elements
 */
export const DividerOverlay: React.FC = () => {
  const state = useGridState()

  // Auto-generate dividers from block configuration
  const dividers = useMemo(() => {
    return generateDividers(state.blocks)
  }, [state.blocks])

  if (dividers.length === 0) {
    return null
  }

  return (
    <div
      className="pretty-poly-divider-overlay"
      style={{
        position: 'absolute',
        inset: 0,
        pointerEvents: 'none',
        zIndex: 10
      }}
    >
      {dividers.map(divider => (
        <Divider
          key={divider.id}
          targetId={divider.targetBlockId}
          position={divider.position}
          direction={divider.direction}
        />
      ))}
    </div>
  )
}

import { useCallback, useEffect } from 'react'
import type { BlockConfig, Direction } from "@/lib/grid-types"
import { useGridResize as useGridResizeContext } from "@/components/grid/grid-provider"
import { getFlexSpacePx } from "@/lib/grid-calculations"

export interface UseGridResizeOptions {
  blocks: BlockConfig[]
  containerRef: React.RefObject<HTMLElement>
  onSizeChange?: (blockId: string, newSize: number) => void
  direction?: Direction
}

/**
 * Hook for handling grid resize operations
 * Now uses the context-based resize system instead of managing state locally
 */
export function useGridResize({
  blocks,
  containerRef,
  onSizeChange,
  direction = 'row'
}: UseGridResizeOptions) {
  const {
    startResize,
    updateResize,
    endResize,
    isDragging,
    activeBlockId,
    activeDividerId
  } = useGridResizeContext()

  /**
   * Get container size in the relevant dimension
   */
  const getContainerSize = useCallback((): number => {
    if (!containerRef.current) return 0

    const rect = containerRef.current.getBoundingClientRect()
    return direction === 'column' ? rect.width : rect.height
  }, [direction, containerRef])

  /**
   * Calculate pixels per fr unit for the current container
   * Note: Dividers are now overlays and don't take up grid space
   */
  const calculatePixelsPerFr = useCallback((): number => {
    const containerSize = getContainerSize()
    if (containerSize === 0) return 0

    // Calculate fixed space (px blocks only - dividers are overlays now)
    const fixedSpace = blocks
      .filter(block => block.sizeUnit === 'px')
      .reduce((sum, block) => sum + (block.defaultSize || 0), 0)

    // No gap space - dividers are overlays
    const gapSpace = 0

    // Calculate total fr units
    const totalFr = blocks
      .filter(block => block.sizeUnit === 'fr')
      .reduce((sum, block) => sum + (block.defaultSize || 1), 0)

    const flexSpace = getFlexSpacePx(containerSize, fixedSpace, gapSpace)
    return totalFr > 0 ? flexSpace / totalFr : 0
  }, [blocks, getContainerSize])

  /**
   * Reset a block to its default size
   */
  const resetBlock = useCallback((blockId: string) => {
    const block = blocks.find(b => b.id === blockId)
    if (block && block.defaultSize !== undefined) {
      onSizeChange?.(blockId, block.defaultSize)
    }
  }, [blocks, onSizeChange])

  /**
   * Collapse a block
   */
  const collapseBlock = useCallback((blockId: string) => {
    const block = blocks.find(b => b.id === blockId)
    if (block && block.collapseTo !== undefined) {
      onSizeChange?.(blockId, block.collapseTo)
    }
  }, [blocks, onSizeChange])

  /**
   * Expand a collapsed block
   */
  const expandBlock = useCallback((blockId: string) => {
    const block = blocks.find(b => b.id === blockId)
    if (block && block.defaultSize !== undefined) {
      onSizeChange?.(blockId, block.defaultSize)
    }
  }, [blocks, onSizeChange])

  /**
   * Check if a block is collapsed
   */
  const isBlockCollapsed = useCallback((blockId: string): boolean => {
    const block = blocks.find(b => b.id === blockId)
    if (!block || !block.collapseAt) return false

    return (block.defaultSize || 0) <= block.collapseAt
  }, [blocks])

  // Set up global event listeners for resize operations
  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      e.preventDefault()
      updateResize(e)
    }

    const handleTouchMove = (e: TouchEvent) => {
      e.preventDefault()
      updateResize(e)
    }

    const handleMouseUp = () => {
      endResize()
    }

    const handleTouchEnd = () => {
      endResize()
    }

    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove)
      document.addEventListener('mouseup', handleMouseUp)
      document.addEventListener('touchmove', handleTouchMove)
      document.addEventListener('touchend', handleTouchEnd)

      return () => {
        document.removeEventListener('mousemove', handleMouseMove)
        document.removeEventListener('mouseup', handleMouseUp)
        document.removeEventListener('touchmove', handleTouchMove)
        document.removeEventListener('touchend', handleTouchEnd)
      }
    }
  }, [isDragging, updateResize, endResize])

  return {
    // State
    isDragging,
    activeBlockId,
    activeDividerId,

    // Actions
    startResize,
    resetBlock,
    collapseBlock,
    expandBlock,

    // Utilities
    isBlockCollapsed,
    getContainerSize,
    calculatePixelsPerFr
  }
}

import { useCallback } from 'react'
import type { GridState, GridAction } from "@/lib/grid-types"
import { calculateConstrainedSize, getFlexSpacePx, pxToFr } from "@/lib/grid-calculations"

/**
 * Hook for managing grid resize operations
 * Extracted from GridProvider to reduce complexity and improve testability
 */
export function useGridResizeOperations(
  state: GridState,
  dispatch: React.Dispatch<GridAction>
) {
  /**
   * Extract position from mouse or touch event
   */
  const getEventPosition = useCallback((
    event: MouseEvent | TouchEvent | React.MouseEvent | React.TouchEvent
  ): { x: number; y: number } => {
    if ("touches" in event) {
      return {
        x: event.touches[0].clientX,
        y: event.touches[0].clientY,
      };
    }
    return {
      x: event.clientX,
      y: event.clientY,
    };
  }, []);

  /**
   * Start a resize operation
   */
  const startResize = useCallback((
    blockId: string,
    dividerId: string,
    event: React.MouseEvent | React.TouchEvent
  ) => {
    const block = state.blocks[blockId];
    if (!block) return;

    const startPosition = getEventPosition(event);

    // Get the divider position from the DOM
    const dividerEl = document.querySelector(`[data-divider-id="${dividerId}"]`);
    const dividerPosition = dividerEl?.getAttribute('data-divider-position') as 'start' | 'end' || 'end';

    // Find adjacent block for two-way resizing
    const siblingBlocks = Object.values(state.blocks).filter(
      (b) => b.parentId === block.parentId
    );
    const sortedSiblings = siblingBlocks.sort(
      (a, b) => (a.order || 0) - (b.order || 0)
    );
    const blockIndex = sortedSiblings.findIndex((b) => b.id === blockId);

    let adjacentBlock = null;
    if (dividerPosition === "start" && blockIndex > 0) {
      adjacentBlock = sortedSiblings[blockIndex - 1];
    } else if (
      dividerPosition === "end" &&
      blockIndex < sortedSiblings.length - 1
    ) {
      adjacentBlock = sortedSiblings[blockIndex + 1];
    }

    // Validation: Prevent resizing fr blocks when adjacent to px blocks
    if (
      adjacentBlock &&
      block.sizeUnit === "fr" &&
      adjacentBlock.sizeUnit === "px"
    ) {
      console.warn(
        `Cannot resize fr block "${blockId}" when adjacent to px block "${adjacentBlock.id}". Fr blocks fill available space and should not be directly resized. Consider resizing the px block instead.`
      );
      return;
    }

    dispatch({
      type: "START_RESIZE",
      payload: {
        blockId,
        dividerId,
        startPosition,
        initialSize: block.defaultSize || 0,
        initialAdjacentBlockId: adjacentBlock?.id,
        initialAdjacentSize: adjacentBlock
          ? adjacentBlock.originalDefaultSize ||
            adjacentBlock.defaultSize ||
            0
          : undefined,
      },
    });

    // Prevent text selection during drag
    document.body.style.userSelect = "none";
    const parentBlock = block.parentId
      ? state.blocks[block.parentId]
      : null;
    const direction = parentBlock?.direction || "row";
    document.body.style.cursor =
      direction === "row" ? "col-resize" : "row-resize";
  }, [state.blocks, dispatch, getEventPosition]);

  /**
   * Update resize during drag
   */
  const updateResize = useCallback((event: MouseEvent | TouchEvent) => {
    if (!state.resize.isDragging || !state.resize.activeBlockId) return;

    const block = state.blocks[state.resize.activeBlockId];
    if (!block) return;

    const currentPosition = getEventPosition(event);

    // Find the parent block to determine the resize direction
    const parentBlock = block.parentId
      ? state.blocks[block.parentId]
      : null;
    const direction = parentBlock?.direction || "row";

    // Calculate delta based on the parent's direction
    // If parent arranges children in 'row', dividers move horizontally (X axis)
    // If parent arranges children in 'column', dividers move vertically (Y axis)
    const deltaPx =
      direction === "row"
        ? currentPosition.x - state.resize.startPosition.x
        : currentPosition.y - state.resize.startPosition.y;

    if (block.sizeUnit === "px") {
      // Handle pixel-based resizing
      // Get position from divider's data attribute
      const dividerEl = document.querySelector(`[data-divider-id="${state.resize.activeDividerId}"]`);
      const dividerPosition = dividerEl?.getAttribute('data-divider-position') || 'end';

      // For position="start": divider is BEFORE the block
      // - Dragging left (negative delta) should GROW the block (need inversion)
      // - Dragging right (positive delta) should SHRINK the block (need inversion)
      // For position="end": divider is AFTER the block
      // - Dragging right (positive delta) should GROW the block (no inversion)
      // - Dragging left (negative delta) should SHRINK the block (no inversion)
      const shouldInvertDelta = dividerPosition === "start";

      const newSize = calculateConstrainedSize(
        deltaPx,
        state.resize.initialSize,
        block.minSize,
        block.maxSize,
        shouldInvertDelta
      );

      dispatch({
        type: "RESIZE_BLOCK",
        payload: { blockId: state.resize.activeBlockId, size: newSize },
      });
    } else if (block.sizeUnit === "fr") {
      // Handle fractional resizing (two-way)
      const siblingBlocks = Object.values(state.blocks).filter(
        (b) => b.parentId === block.parentId
      );
      const frBlocks = siblingBlocks.filter((b) => b.sizeUnit === "fr");

      // Measure the actual parent container dimensions
      const parentElement = block.parentId
        ? document.querySelector(`[data-block-id="${block.parentId}"]`)
        : document.querySelector(`[data-block-id="root"]`);

      const containerSize = parentElement
        ? direction === "row"
          ? parentElement.clientWidth
          : parentElement.clientHeight
        : 1200; // Fallback only if element not found
      // Measure actual pixel space used by px blocks (not configured defaultSize)
      const pxBlocks = siblingBlocks
        .filter((b) => b.sizeUnit === "px")
        .reduce((sum, b) => {
          const el = document.querySelector(`[data-block-id="${b.id}"]`);
          if (!el) return sum;
          const size =
            direction === "row" ? el.clientWidth : el.clientHeight;
          return sum + size;
        }, 0);

      // Measure actual divider sizes from DOM (not calculated count  8px)
      const dividers = Array.from(
        parentElement?.querySelectorAll('[data-block-type="divider"]') || []
      );
      const gapSpace = dividers.reduce((sum, el) => {
        if (!(el instanceof HTMLElement)) return sum;
        const size = direction === "row" ? el.clientWidth : el.clientHeight;
        return sum + size;
      }, 0);

      const fixedSpace = pxBlocks;
      const flexSpace = getFlexSpacePx(containerSize, fixedSpace, gapSpace);
      // Use current sizes, not original defaults, for accurate calculations during multi-step resizes
      const totalFr = frBlocks.reduce(
        (sum, b) => sum + (b.defaultSize || 1),
        0
      );
      const pixelsPerFr = totalFr > 0 ? flexSpace / totalFr : 0;

      if (pixelsPerFr === 0) return; // Prevent division by zero

      const deltaFr = pxToFr(deltaPx, pixelsPerFr);

      // Find adjacent block based on block order
      const sortedFrBlocks = frBlocks.sort(
        (a, b) => (a.order || 0) - (b.order || 0)
      );
      const blockIndex = sortedFrBlocks.findIndex(
        (b) => b.id === state.resize.activeBlockId
      );

      // Get actual position from divider's data attribute first
      const dividerEl = document.querySelector(`[data-divider-id="${state.resize.activeDividerId}"]`);
      const dividerPosition = dividerEl?.getAttribute('data-divider-position') || 'end';

      let adjacentBlock = null;
      if (dividerPosition === "start" && blockIndex > 0) {
        adjacentBlock = sortedFrBlocks[blockIndex - 1];
      } else if (
        dividerPosition === "end" &&
        blockIndex < sortedFrBlocks.length - 1
      ) {
        adjacentBlock = sortedFrBlocks[blockIndex + 1];
      }

      if (adjacentBlock) {
        let targetDelta: number;
        let adjacentDelta: number;

        if (dividerPosition === "start") {
          // Divider at start: moving right grows target, shrinks previous
          targetDelta = deltaFr;
          adjacentDelta = -deltaFr;
        } else {
          // Divider at end: moving right grows target, shrinks next
          targetDelta = deltaFr;
          adjacentDelta = -deltaFr;
        }

        // Apply minimum size constraints using initial sizes
        const minSize = 0.1; // Minimum fr size
        const newTargetSize = Math.max(
          minSize,
          state.resize.initialSize + targetDelta
        );
        const newAdjacentSize = Math.max(
          minSize,
          (state.resize.initialAdjacentSize || 1) + adjacentDelta
        );

        // Ensure zero-sum by adjusting if constraints were applied
        const actualTargetDelta = newTargetSize - state.resize.initialSize;
        const actualAdjacentDelta =
          newAdjacentSize - (state.resize.initialAdjacentSize || 1);

        if (Math.abs(actualTargetDelta + actualAdjacentDelta) < 0.01) {
          dispatch({
            type: "RESIZE_BLOCK",
            payload: {
              blockId: state.resize.activeBlockId,
              size: newTargetSize,
            },
          });
          dispatch({
            type: "RESIZE_BLOCK",
            payload: { blockId: adjacentBlock.id, size: newAdjacentSize },
          });
        }
      }
    }
  }, [state.resize, state.blocks, dispatch, getEventPosition]);

  /**
   * End resize operation and cleanup
   */
  const endResize = useCallback(() => {
    dispatch({ type: "END_RESIZE" });

    // Restore normal cursor and text selection
    document.body.style.userSelect = "";
    document.body.style.cursor = "";
  }, [dispatch]);

  return {
    startResize,
    updateResize,
    endResize,
  };
}

import { useState, useEffect, useCallback, useMemo } from 'react'
import type { ResponsiveModes, LayoutMode, ViewportInfo } from "@/lib/grid-types"

/**
 * Default responsive mode configurations
 */
export const defaultModes: ResponsiveModes = {
  mobile: {
    type: 'dock',
    breakpoint: 0,
    maxWidth: 767
  },
  tablet: {
    type: 'tabs',
    breakpoint: 768,
    minWidth: 768,
    maxWidth: 1023
  },
  desktop: {
    type: 'grid',
    breakpoint: 1024,
    minWidth: 1024
  }
}

/**
 * Hook for managing responsive grid modes
 */
export function useGridMode(modes: ResponsiveModes = defaultModes) {
  const [viewport, setViewport] = useState<ViewportInfo>(() => {
    if (typeof window === 'undefined') {
      return { width: 1024, height: 768, orientation: 'landscape' }
    }

    return {
      width: window.innerWidth,
      height: window.innerHeight,
      orientation: window.innerWidth > window.innerHeight ? 'landscape' : 'portrait'
    }
  })

  const [forcedMode, setForcedMode] = useState<string | null>(null)

  /**
   * Update viewport information
   */
  const updateViewport = useCallback(() => {
    if (typeof window === 'undefined') return

    const newViewport: ViewportInfo = {
      width: window.innerWidth,
      height: window.innerHeight,
      orientation: window.innerWidth > window.innerHeight ? 'landscape' : 'portrait'
    }

    setViewport(newViewport)
  }, [])

  /**
   * Find the active mode based on current viewport and modes configuration
   */
  const activeMode = useMemo(() => {
    if (forcedMode && modes[forcedMode]) {
      return forcedMode
    }

    // Find the best matching mode
    const matchingModes = Object.entries(modes).filter(([_, config]) => {
      // If mode has a custom matcher, use it
      if (config.matcher) {
        return config.matcher(viewport)
      }

      // Otherwise use breakpoint logic
      const matchesMin = !config.minWidth || viewport.width >= config.minWidth
      const matchesMax = !config.maxWidth || viewport.width <= config.maxWidth

      return matchesMin && matchesMax
    })

    // Sort by specificity (modes with both min and max are more specific)
    matchingModes.sort(([, a], [, b]) => {
      const aSpecific = (a.minWidth ? 1 : 0) + (a.maxWidth ? 1 : 0)
      const bSpecific = (b.minWidth ? 1 : 0) + (b.maxWidth ? 1 : 0)
      return bSpecific - aSpecific
    })

    // Return the most specific matching mode, or fall back to first available
    return matchingModes[0]?.[0] || Object.keys(modes)[0] || 'desktop'
  }, [viewport, modes, forcedMode])

  /**
   * Get the current mode configuration
   */
  const currentModeConfig = useMemo(() => {
    return modes[activeMode]
  }, [modes, activeMode])

  /**
   * Get the current layout type
   */
  const currentLayoutType = useMemo(() => {
    return currentModeConfig?.type || 'grid'
  }, [currentModeConfig])

  /**
   * Force a specific mode (useful for testing or user preference)
   */
  const setMode = useCallback((modeName: string | null) => {
    if (modeName && !modes[modeName]) {
      console.warn(`Mode "${modeName}" not found in configuration`)
      return
    }
    setForcedMode(modeName)
  }, [modes])

  /**
   * Check if a specific mode is currently active
   */
  const isMode = useCallback((modeName: string) => {
    return activeMode === modeName
  }, [activeMode])

  /**
   * Get all available mode names
   */
  const availableModes = useMemo(() => {
    return Object.keys(modes)
  }, [modes])

  /**
   * Check if the current mode supports a specific feature
   */
  const supportsFeature = useCallback((feature: 'resizing' | 'dividers' | 'collapse' | 'tabs' | 'dock'): boolean => {
    switch (currentLayoutType) {
      case 'grid':
        return ['resizing', 'dividers', 'collapse'].includes(feature)
      case 'tabs':
        return feature === 'tabs'
      case 'dock':
        return feature === 'dock'
      default:
        return false
    }
  }, [currentLayoutType])

  /**
   * Get responsive breakpoint information
   */
  const breakpointInfo = useMemo(() => {
    const sortedModes = Object.entries(modes)
      .map(([name, config]) => ({ name, ...config }))
      .sort((a, b) => (a.breakpoint || 0) - (b.breakpoint || 0))

    const currentIndex = sortedModes.findIndex(mode => mode.name === activeMode)
    const nextMode = sortedModes[currentIndex + 1]
    const prevMode = sortedModes[currentIndex - 1]

    return {
      current: activeMode,
      currentBreakpoint: currentModeConfig?.breakpoint || 0,
      nextMode: nextMode?.name,
      nextBreakpoint: nextMode?.breakpoint,
      prevMode: prevMode?.name,
      prevBreakpoint: prevMode?.breakpoint,
      isSmallest: currentIndex === 0,
      isLargest: currentIndex === sortedModes.length - 1
    }
  }, [modes, activeMode, currentModeConfig])

  // Set up viewport change listener
  useEffect(() => {
    if (typeof window === 'undefined') return

    const handleResize = () => updateViewport()
    const handleOrientationChange = () => {
      // Delay to allow orientation change to complete
      setTimeout(updateViewport, 100)
    }

    window.addEventListener('resize', handleResize)
    window.addEventListener('orientationchange', handleOrientationChange)

    return () => {
      window.removeEventListener('resize', handleResize)
      window.removeEventListener('orientationchange', handleOrientationChange)
    }
  }, [updateViewport])

  return {
    // Current state
    viewport,
    activeMode,
    currentModeConfig,
    currentLayoutType: currentLayoutType as LayoutMode,

    // Mode management
    setMode,
    isMode,
    availableModes,

    // Features and capabilities
    supportsFeature,
    breakpointInfo,

    // Utilities
    isForced: !!forcedMode,
    updateViewport
  }
}

import { useCallback, useEffect, useRef } from 'react'
import type { GridState } from "@/lib/grid-types"
import {
  getStorageAdapter,
  saveGridState,
  loadGridState,
  removeGridState,
  createCustomAdapter,
  type StorageAdapter
} from "@/lib/grid-storage"

export interface UseGridPersistenceOptions {
  gridId: string
  enabled: boolean | 'localStorage' | 'sessionStorage' | ((state: GridState) => void)
  state: GridState
  onStateLoad?: (state: Partial<GridState>) => void
  autoSave?: boolean
  saveDelay?: number
}

/**
 * Hook for handling grid state persistence
 */
export function useGridPersistence({
  gridId,
  enabled,
  state,
  onStateLoad,
  autoSave = true,
  saveDelay = 500
}: UseGridPersistenceOptions) {
  const storageAdapter = useRef<StorageAdapter | null>(null)
  const saveTimeoutRef = useRef<NodeJS.Timeout>()
  const lastSavedState = useRef<string>('')
  const hasLoadedRef = useRef<boolean>(false)

  // Initialize storage adapter
  useEffect(() => {
    if (!enabled) {
      storageAdapter.current = null
      return
    }

    if (typeof enabled === 'function') {
      // Custom save function
      storageAdapter.current = createCustomAdapter(enabled)
    } else if (enabled === 'localStorage') {
      storageAdapter.current = getStorageAdapter('localStorage')
    } else if (enabled === 'sessionStorage') {
      storageAdapter.current = getStorageAdapter('sessionStorage')
    } else {
      // Default to localStorage
      storageAdapter.current = getStorageAdapter('localStorage')
    }
  }, [enabled])

  /**
   * Load persisted state on mount
   */
  useEffect(() => {
    if (!storageAdapter.current || !enabled || typeof enabled === 'function' || hasLoadedRef.current) {
      return
    }

    const loadedState = loadGridState(gridId, storageAdapter.current)
    if (loadedState) {
      onStateLoad?.(loadedState)
      hasLoadedRef.current = true
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [gridId, enabled]) // onStateLoad is intentionally stable (not expected to change)

  /**
   * Save state to storage
   */
  const saveState = useCallback((stateToSave: GridState = state) => {
    if (!storageAdapter.current || !enabled) {
      return
    }

    // Don't save if state hasn't changed
    const stateString = JSON.stringify(stateToSave)
    if (stateString === lastSavedState.current) {
      return
    }

    try {
      saveGridState(gridId, stateToSave, storageAdapter.current)
      lastSavedState.current = stateString
    } catch (error) {
      console.warn('Failed to save grid state:', error)
    }
  }, [gridId, enabled, state])

  /**
   * Save state with debouncing
   */
  const debouncedSave = useCallback((stateToSave: GridState = state) => {
    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current)
    }

    saveTimeoutRef.current = setTimeout(() => {
      saveState(stateToSave)
    }, saveDelay)
  }, [saveState, saveDelay, state])

  /**
   * Clear persisted state
   */
  const clearState = useCallback(() => {
    if (!storageAdapter.current || !enabled || typeof enabled === 'function') {
      return
    }

    try {
      removeGridState(gridId, storageAdapter.current)
      lastSavedState.current = ''
    } catch (error) {
      console.warn('Failed to clear grid state:', error)
    }
  }, [gridId, enabled])

  /**
   * Force immediate save
   */
  const forceSave = useCallback((stateToSave: GridState = state) => {
    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current)
    }
    saveState(stateToSave)
  }, [saveState, state])

  // Auto-save when state changes
  useEffect(() => {
    if (!autoSave || !enabled) {
      return
    }

    debouncedSave(state)

    // Cleanup timeout on unmount
    return () => {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current)
      }
    }
  }, [state, autoSave, enabled, debouncedSave])

  // Save state before page unload
  useEffect(() => {
    if (!enabled || typeof enabled === 'function') {
      return
    }

    const handleBeforeUnload = () => {
      forceSave()
    }

    window.addEventListener('beforeunload', handleBeforeUnload)
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload)
    }
  }, [forceSave, enabled])

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current)
      }
    }
  }, [])

  return {
    saveState: forceSave,
    debouncedSave,
    clearState,
    isEnabled: !!enabled
  }
}

import { useCallback, useEffect } from 'react'
import type { BlockConfig } from "@/lib/grid-types"

export interface UseGridKeyboardOptions {
  enabled?: boolean
  blocks: BlockConfig[]
  onResizeBlock?: (blockId: string, delta: number) => void
  onFocusBlock?: (blockId: string) => void
  onCollapseBlock?: (blockId: string) => void
  onExpandBlock?: (blockId: string) => void
  containerRef?: React.RefObject<HTMLElement>
  stepSize?: number
  largeStepSize?: number
}

/**
 * Hook for keyboard navigation and control of grid layouts
 */
export function useGridKeyboard({
  enabled = true,
  blocks,
  onResizeBlock,
  onFocusBlock,
  onCollapseBlock,
  onExpandBlock,
  containerRef,
  stepSize = 10,
  largeStepSize = 50
}: UseGridKeyboardOptions) {

  /**
   * Get the currently focused block element
   */
  const getFocusedBlock = useCallback((): HTMLElement | null => {
    const activeElement = document.activeElement as HTMLElement

    // Check if the active element is a block
    if (activeElement?.dataset.blockType === 'block' || activeElement?.dataset.blockType === 'group') {
      return activeElement
    }

    // Check if active element is within a block
    const blockElement = activeElement?.closest('[data-block-type="block"], [data-block-type="group"]') as HTMLElement
    return blockElement || null
  }, [])

  /**
   * Get the block configuration for a given element
   */
  const getBlockConfig = useCallback((element: HTMLElement | null): BlockConfig | null => {
    if (!element) return null

    const blockId = element.dataset.blockId
    return blocks.find(block => block.id === blockId) || null
  }, [blocks])

  /**
   * Find the next focusable block in a given direction
   */
  const findNextBlock = useCallback((
    currentBlock: HTMLElement,
    direction: 'up' | 'down' | 'left' | 'right'
  ): HTMLElement | null => {
    if (!containerRef?.current) return null

    const allBlocks = Array.from(
      containerRef.current.querySelectorAll('[data-block-type="block"], [data-block-type="group"]')
    ) as HTMLElement[]

    const currentRect = currentBlock.getBoundingClientRect()
    const currentCenter = {
      x: currentRect.left + currentRect.width / 2,
      y: currentRect.top + currentRect.height / 2
    }

    // Filter blocks based on direction
    const candidates = allBlocks.filter(block => {
      if (block === currentBlock) return false

      const rect = block.getBoundingClientRect()
      const center = {
        x: rect.left + rect.width / 2,
        y: rect.top + rect.height / 2
      }

      switch (direction) {
        case 'up':
          return center.y < currentCenter.y
        case 'down':
          return center.y > currentCenter.y
        case 'left':
          return center.x < currentCenter.x
        case 'right':
          return center.x > currentCenter.x
        default:
          return false
      }
    })

    // Find the closest candidate
    if (candidates.length === 0) return null

    candidates.sort((a, b) => {
      const aRect = a.getBoundingClientRect()
      const bRect = b.getBoundingClientRect()
      const aCenter = {
        x: aRect.left + aRect.width / 2,
        y: aRect.top + aRect.height / 2
      }
      const bCenter = {
        x: bRect.left + bRect.width / 2,
        y: bRect.top + bRect.height / 2
      }

      // Calculate distance
      const aDist = Math.sqrt(
        Math.pow(aCenter.x - currentCenter.x, 2) +
        Math.pow(aCenter.y - currentCenter.y, 2)
      )
      const bDist = Math.sqrt(
        Math.pow(bCenter.x - currentCenter.x, 2) +
        Math.pow(bCenter.y - currentCenter.y, 2)
      )

      return aDist - bDist
    })

    return candidates[0]
  }, [containerRef])

  /**
   * Handle keyboard events
   */
  const handleKeyDown = useCallback((event: KeyboardEvent) => {
    if (!enabled) return

    const focusedBlock = getFocusedBlock()
    if (!focusedBlock) return

    const blockConfig = getBlockConfig(focusedBlock)
    if (!blockConfig) return

    const isCtrlOrCmd = event.ctrlKey || event.metaKey
    const isShift = event.shiftKey
    const currentStepSize = isShift ? largeStepSize : stepSize

    // Navigation (arrow keys without modifiers)
    if (!isCtrlOrCmd && !isShift) {
      switch (event.key) {
        case 'ArrowUp':
          event.preventDefault()
          const upBlock = findNextBlock(focusedBlock, 'up')
          if (upBlock) {
            upBlock.focus()
            onFocusBlock?.(upBlock.dataset.blockId || '')
          }
          break

        case 'ArrowDown':
          event.preventDefault()
          const downBlock = findNextBlock(focusedBlock, 'down')
          if (downBlock) {
            downBlock.focus()
            onFocusBlock?.(downBlock.dataset.blockId || '')
          }
          break

        case 'ArrowLeft':
          event.preventDefault()
          const leftBlock = findNextBlock(focusedBlock, 'left')
          if (leftBlock) {
            leftBlock.focus()
            onFocusBlock?.(leftBlock.dataset.blockId || '')
          }
          break

        case 'ArrowRight':
          event.preventDefault()
          const rightBlock = findNextBlock(focusedBlock, 'right')
          if (rightBlock) {
            rightBlock.focus()
            onFocusBlock?.(rightBlock.dataset.blockId || '')
          }
          break

        case 'Enter':
        case ' ':
          event.preventDefault()
          // Toggle collapse/expand
          if (blockConfig.collapsible) {
            // Check if currently collapsed (this would need to be provided by parent)
            // For now, just trigger expand
            onExpandBlock?.(blockConfig.id)
          }
          break

        case 'Escape':
          event.preventDefault()
          focusedBlock.blur()
          break
      }
    }

    // Resizing (Ctrl/Cmd + arrow keys)
    if (isCtrlOrCmd && onResizeBlock) {
      switch (event.key) {
        case 'ArrowUp':
          event.preventDefault()
          onResizeBlock(blockConfig.id, -currentStepSize)
          break

        case 'ArrowDown':
          event.preventDefault()
          onResizeBlock(blockConfig.id, currentStepSize)
          break

        case 'ArrowLeft':
          event.preventDefault()
          onResizeBlock(blockConfig.id, -currentStepSize)
          break

        case 'ArrowRight':
          event.preventDefault()
          onResizeBlock(blockConfig.id, currentStepSize)
          break
      }
    }

    // Collapse/Expand (Ctrl/Cmd + specific keys)
    if (isCtrlOrCmd) {
      switch (event.key) {
        case 'Minus':
        case '-':
          event.preventDefault()
          onCollapseBlock?.(blockConfig.id)
          break

        case 'Plus':
        case '+':
        case '=':
          event.preventDefault()
          onExpandBlock?.(blockConfig.id)
          break
      }
    }
  }, [
    enabled,
    getFocusedBlock,
    getBlockConfig,
    findNextBlock,
    onResizeBlock,
    onFocusBlock,
    onCollapseBlock,
    onExpandBlock,
    stepSize,
    largeStepSize
  ])

  /**
   * Focus a specific block
   */
  const focusBlock = useCallback((blockId: string) => {
    if (!containerRef?.current) return

    const blockElement = containerRef.current.querySelector(
      `[data-block-id="${blockId}"]`
    ) as HTMLElement

    if (blockElement) {
      blockElement.focus()
      onFocusBlock?.(blockId)
    }
  }, [containerRef, onFocusBlock])

  /**
   * Get all focusable blocks
   */
  const getFocusableBlocks = useCallback((): HTMLElement[] => {
    if (!containerRef?.current) return []

    return Array.from(
      containerRef.current.querySelectorAll(
        '[data-block-type="block"][tabindex], [data-block-type="group"][tabindex]'
      )
    ) as HTMLElement[]
  }, [containerRef])

  // Set up keyboard event listeners
  useEffect(() => {
    if (!enabled) return

    document.addEventListener('keydown', handleKeyDown)

    return () => {
      document.removeEventListener('keydown', handleKeyDown)
    }
  }, [handleKeyDown, enabled])

  return {
    focusBlock,
    getFocusableBlocks,
    getFocusedBlock,
    isEnabled: enabled
  }
}

/**
 * Grid calculation utilities ported from grid_resize.ts
 */

/**
 * Calculate available flex space in pixels
 * @param containerPx Total container size in pixels
 * @param fixedTrackPx Sum of all fixed (px) tracks
 * @param gapsPx Sum of all gap sizes
 * @returns Available space for fr units
 */
export function getFlexSpacePx(
  containerPx: number,
  fixedTrackPx: number,
  gapsPx: number
): number {
  return Math.max(0, containerPx - fixedTrackPx - gapsPx)
}

/**
 * Calculate pixels per fr unit
 * @param flexSpacePx Available flex space in pixels
 * @param totalFr Total fr units in the container
 * @returns Pixels per fr unit
 */
export function pxPerFr(flexSpacePx: number, totalFr: number): number {
  return totalFr > 0 ? flexSpacePx / totalFr : 0
}

/**
 * Clamp a value between min and max bounds
 * @param value Value to clamp
 * @param min Minimum value
 * @param max Maximum value
 * @returns Clamped value
 */
export function clamp(value: number, min: number, max: number): number {
  return Math.min(Math.max(value, min), max)
}

/**
 * Check if a value is collapsed based on threshold
 * @param value Current value
 * @param collapseAt Threshold for collapsing
 * @returns True if value should be considered collapsed
 */
export function isCollapsed(value: number, collapseAt: number): boolean {
  return collapseAt > 0 && value <= collapseAt
}

/**
 * Apply collapse logic to a resize operation
 * @param newSize Proposed new size
 * @param currentSize Current size
 * @param collapseAt Collapse threshold
 * @param collapseTo Size to collapse to
 * @param defaultSize Default size to expand to
 * @returns Adjusted size with collapse logic applied
 */
export function applyCollapseLogic(
  newSize: number,
  currentSize: number,
  collapseAt: number,
  collapseTo: number,
  defaultSize: number
): number {
  // Skip if collapsing is disabled
  if (collapseAt === 0) {
    return newSize
  }

  const isCurrentlyCollapsed = currentSize <= collapseAt

  // If currently collapsed and trying to expand significantly, snap to default
  // Use a threshold: expand if newSize is more than double collapseTo
  const expandThreshold = collapseTo * 2.5
  if (isCurrentlyCollapsed && newSize > expandThreshold) {
    return defaultSize
  }

  // If new size would be below threshold, collapse it
  // But if we're already collapsed, don't force further collapse
  if (newSize < collapseAt && !isCurrentlyCollapsed) {
    return collapseTo
  }

  return newSize
}

/**
 * Calculate new size with constraints applied
 * @param deltaPx Change in pixels
 * @param initialSize Initial size value
 * @param minSize Minimum allowed size
 * @param maxSize Maximum allowed size
 * @param invertDelta Whether to invert the delta (for start dividers)
 * @returns New size with constraints applied
 */
export function calculateConstrainedSize(
  deltaPx: number,
  initialSize: number,
  minSize: number = 0,
  maxSize: number = Infinity,
  invertDelta: boolean = false
): number {
  const adjustedDelta = invertDelta ? -deltaPx : deltaPx
  const rawNewSize = initialSize + adjustedDelta

  return clamp(rawNewSize, minSize, maxSize)
}

/**
 * Generate CSS grid template value without dividers (dividers are now overlays)
 * @param blocks Block configurations
 * @param gridId Grid ID for scoping CSS variables
 * @returns CSS grid-template string
 */
export function generateGridTemplate(
  blocks: Array<{
    id: string
    sizeUnit: "px" | "fr" | "auto"
    size: number | "auto"
    dividerPosition?: "start" | "end" | "none"
    dividerSize?: number
  }>,
  gridId?: string
): string {
  const parts: string[] = []

  blocks.forEach(block => {
    // Add the block itself (no dividers - they're overlays now)
    if (block.sizeUnit === "auto") {
      parts.push("auto")
    } else if (block.sizeUnit === "px") {
      const varName = gridId ? `--${gridId}-${block.id}-size` : `--${block.id}-size`
      parts.push(`var(${varName}, ${block.size}px)`)
    } else {
      const varName = gridId ? `--${gridId}-${block.id}-size` : `--${block.id}-size`
      parts.push(`var(${varName}, ${block.size}fr)`)
    }
  })

  return parts.join(" ")
}

/**
 * Generate CSS grid template from template items (for auto divider mode)
 * Note: Dividers are now overlays, so they're excluded from grid template
 * @param items Template items including blocks and dividers
 * @param gridId Grid ID for scoping CSS variables
 * @returns CSS grid-template string
 */
export function generateGridTemplateFromItems(
  items: Array<{
    id: string
    type: 'block' | 'divider'
    sizeUnit?: 'px' | 'fr' | 'auto'
    size?: number
    dividerSize?: number
  }>,
  gridId?: string
): string {
  const parts: string[] = []

  items.forEach(item => {
    // Skip dividers - they're overlays now
    if (item.type === 'divider') {
      return
    }

    // Block with CSS variable support
    if (item.sizeUnit === 'auto') {
      parts.push('auto')
    } else if (item.sizeUnit === 'px') {
      const varName = gridId ? `--${gridId}-${item.id}-size` : `--${item.id}-size`
      parts.push(`var(${varName}, ${item.size || 1}px)`)
    } else {
      const varName = gridId ? `--${gridId}-${item.id}-size` : `--${item.id}-size`
      parts.push(`var(${varName}, ${item.size || 1}fr)`)
    }
  })

  return parts.join(' ')
}

/**
 * Convert fractional resize delta to pixels
 * @param deltaFr Change in fr units
 * @param pixelsPerFr Pixels per fr unit
 * @returns Delta in pixels
 */
export function frToPx(deltaFr: number, pixelsPerFr: number): number {
  return deltaFr * pixelsPerFr
}

/**
 * Convert pixel delta to fractional units
 * @param deltaPx Change in pixels
 * @param pixelsPerFr Pixels per fr unit
 * @returns Delta in fr units
 */
export function pxToFr(deltaPx: number, pixelsPerFr: number): number {
  return pixelsPerFr > 0 ? deltaPx / pixelsPerFr : 0
}

/**
 * Find adjacent block for two-way resizing
 * @param targetIndex Index of target block
 * @param blocks Array of blocks
 * @param position Divider position ("start" or "end")
 * @returns Adjacent block or null
 */
export function findAdjacentBlock<T>(
  targetIndex: number,
  blocks: T[],
  position: "start" | "end"
): T | null {
  if (position === "start") {
    return targetIndex > 0 ? blocks[targetIndex - 1] : null
  } else {
    return targetIndex < blocks.length - 1 ? blocks[targetIndex + 1] : null
  }
}

/**
 * Validate that two deltas maintain zero-sum constraint
 * @param targetDelta Change in target size
 * @param adjacentDelta Change in adjacent size
 * @param tolerance Acceptable deviation from zero sum
 * @returns True if zero-sum is maintained within tolerance
 */
export function isZeroSum(
  targetDelta: number,
  adjacentDelta: number,
  tolerance: number = 0.001
): boolean {
  return Math.abs(targetDelta + adjacentDelta) <= tolerance
}

import type { BlockConfig, SizeUnit } from "@/lib/grid-types"
import { clamp, applyCollapseLogic } from "@/lib/grid-calculations"

/**
 * Constraint validation and enforcement utilities
 */

export interface ConstraintResult {
  isValid: boolean
  adjustedValue: number
  violations: string[]
}

/**
 * Validate and adjust a block size against its constraints
 * @param newSize Proposed new size
 * @param block Block configuration with constraints
 * @param currentSize Current size (for collapse logic)
 * @returns Constraint validation result
 */
export function validateBlockSize(
  newSize: number,
  block: BlockConfig,
  currentSize?: number
): ConstraintResult {
  const violations: string[] = []
  let adjustedValue = newSize

  // Basic range constraints
  const minSize = block.minSize ?? 0
  const maxSize = block.maxSize ?? Infinity

  if (newSize < minSize) {
    violations.push(`Size ${newSize} is below minimum ${minSize}`)
    adjustedValue = minSize
  }

  if (newSize > maxSize) {
    violations.push(`Size ${newSize} exceeds maximum ${maxSize}`)
    adjustedValue = maxSize
  }

  // Apply min/max clamping
  adjustedValue = clamp(adjustedValue, minSize, maxSize)

  // Apply collapse logic for px units
  if (block.sizeUnit === 'px' && block.collapsible && currentSize !== undefined) {
    const collapseAt = block.collapseAt ?? 0
    const collapseTo = block.collapseTo ?? 0
    const defaultSize = block.defaultSize ?? adjustedValue

    adjustedValue = applyCollapseLogic(
      adjustedValue,
      currentSize,
      collapseAt,
      collapseTo,
      defaultSize
    )
  }

  return {
    isValid: violations.length === 0,
    adjustedValue,
    violations
  }
}

/**
 * Validate two-way resize constraints (zero-sum)
 * @param targetBlock Target block configuration
 * @param adjacentBlock Adjacent block configuration
 * @param targetDelta Proposed change to target
 * @param adjacentDelta Proposed change to adjacent
 * @param pixelsPerFr Pixels per fr unit (for fr calculations)
 * @returns Validation result with adjusted deltas
 */
export function validateTwoWayResize(
  targetBlock: BlockConfig,
  adjacentBlock: BlockConfig,
  targetDelta: number,
  adjacentDelta: number,
  pixelsPerFr: number = 1
): {
  isValid: boolean
  adjustedTargetDelta: number
  adjustedAdjacentDelta: number
  violations: string[]
} {
  const violations: string[] = []
  let adjustedTargetDelta = targetDelta
  let adjustedAdjacentDelta = adjacentDelta

  // Convert constraints to common unit (pixels)
  const targetMin = getConstraintInPx(targetBlock.minSize ?? 0, targetBlock.sizeUnit, pixelsPerFr)
  const targetMax = getConstraintInPx(targetBlock.maxSize ?? Infinity, targetBlock.sizeUnit, pixelsPerFr)
  const adjacentMin = getConstraintInPx(adjacentBlock.minSize ?? 0, adjacentBlock.sizeUnit, pixelsPerFr)
  const adjacentMax = getConstraintInPx(adjacentBlock.maxSize ?? Infinity, adjacentBlock.sizeUnit, pixelsPerFr)

  const currentTargetSize = getConstraintInPx(targetBlock.defaultSize ?? 0, targetBlock.sizeUnit, pixelsPerFr)
  const currentAdjacentSize = getConstraintInPx(adjacentBlock.defaultSize ?? 0, adjacentBlock.sizeUnit, pixelsPerFr)

  // Calculate proposed new sizes
  const newTargetSize = currentTargetSize + targetDelta
  const newAdjacentSize = currentAdjacentSize + adjacentDelta

  // Check individual constraints
  let constrainedTargetSize = clamp(newTargetSize, targetMin, targetMax)
  let constrainedAdjacentSize = clamp(newAdjacentSize, adjacentMin, adjacentMax)

  // Adjust deltas based on constraints
  adjustedTargetDelta = constrainedTargetSize - currentTargetSize
  adjustedAdjacentDelta = constrainedAdjacentSize - currentAdjacentSize

  // Ensure zero-sum by adjusting the less constrained block
  const deltaSum = adjustedTargetDelta + adjustedAdjacentDelta

  if (Math.abs(deltaSum) > 0.001) {
    // One block hit a constraint, adjust the other to maintain zero-sum
    const targetHitConstraint = Math.abs(adjustedTargetDelta) < Math.abs(targetDelta)
    const adjacentHitConstraint = Math.abs(adjustedAdjacentDelta) < Math.abs(adjacentDelta)

    if (targetHitConstraint && !adjacentHitConstraint) {
      // Target hit constraint, adjust adjacent to compensate
      const compensatedAdjacentSize = currentAdjacentSize - adjustedTargetDelta
      constrainedAdjacentSize = clamp(compensatedAdjacentSize, adjacentMin, adjacentMax)
      adjustedAdjacentDelta = constrainedAdjacentSize - currentAdjacentSize
    } else if (adjacentHitConstraint && !targetHitConstraint) {
      // Adjacent hit constraint, adjust target to compensate
      const compensatedTargetSize = currentTargetSize - adjustedAdjacentDelta
      constrainedTargetSize = clamp(compensatedTargetSize, targetMin, targetMax)
      adjustedTargetDelta = constrainedTargetSize - currentTargetSize
    }

    violations.push('Zero-sum constraint violated, deltas adjusted')
  }

  // Final zero-sum check
  const finalSum = adjustedTargetDelta + adjustedAdjacentDelta
  const isValid = Math.abs(finalSum) <= 0.001

  return {
    isValid,
    adjustedTargetDelta,
    adjustedAdjacentDelta,
    violations
  }
}

/**
 * Convert a constraint value to pixels
 * @param value Constraint value
 * @param unit Size unit
 * @param pixelsPerFr Conversion rate for fr units
 * @returns Value in pixels
 */
function getConstraintInPx(
  value: number | undefined,
  unit: SizeUnit | undefined,
  pixelsPerFr: number
): number {
  if (value === undefined || value === Infinity) {
    return Infinity
  }

  switch (unit) {
    case 'px':
      return value
    case 'fr':
      return value * pixelsPerFr
    case 'auto':
    default:
      return value
  }
}

/**
 * Check if a resize operation would create invalid layout
 * @param blocks All blocks in the container
 * @param changes Proposed size changes
 * @returns Validation result
 */
export function validateLayoutIntegrity(
  blocks: BlockConfig[],
  changes: Record<string, number>
): {
  isValid: boolean
  violations: string[]
} {
  const violations: string[] = []

  // Check for overlapping constraints
  const totalFixedSize = blocks
    .filter(block => block.sizeUnit === 'px')
    .reduce((sum, block) => sum + (changes[block.id] ?? block.defaultSize ?? 0), 0)

  const totalFrUnits = blocks
    .filter(block => block.sizeUnit === 'fr')
    .reduce((sum, block) => sum + (changes[block.id] ?? block.defaultSize ?? 0), 0)

  // Basic sanity checks
  if (totalFixedSize < 0) {
    violations.push('Total fixed size cannot be negative')
  }

  if (totalFrUnits <= 0 && blocks.some(block => block.sizeUnit === 'fr')) {
    violations.push('Total fr units must be positive')
  }

  // Check for constraint conflicts
  blocks.forEach(block => {
    const newSize = changes[block.id] ?? block.defaultSize ?? 0
    const minSize = block.minSize ?? 0
    const maxSize = block.maxSize ?? Infinity

    if (minSize > maxSize) {
      violations.push(`Block ${block.id}: minSize (${minSize}) > maxSize (${maxSize})`)
    }

    if (newSize < minSize || newSize > maxSize) {
      violations.push(`Block ${block.id}: size ${newSize} violates constraints [${minSize}, ${maxSize}]`)
    }
  })

  return {
    isValid: violations.length === 0,
    violations
  }
}

import type { GridState } from "@/lib/grid-types"

/**
 * Storage utilities for grid state persistence
 */

const STORAGE_KEY_PREFIX = 'pretty-poly-grid-'

export interface StorageAdapter {
  save: (key: string, data: unknown) => void
  load: (key: string) => unknown | null
  remove: (key: string) => void
  clear: () => void
}

/**
 * localStorage adapter
 */
export const localStorageAdapter: StorageAdapter = {
  save: (key: string, data: unknown) => {
    try {
      localStorage.setItem(key, JSON.stringify(data))
    } catch (error) {
      console.warn('Failed to save to localStorage:', error)
    }
  },

  load: (key: string) => {
    try {
      const stored = localStorage.getItem(key)
      return stored ? JSON.parse(stored) : null
    } catch (error) {
      console.warn('Failed to load from localStorage:', error)
      return null
    }
  },

  remove: (key: string) => {
    try {
      localStorage.removeItem(key)
    } catch (error) {
      console.warn('Failed to remove from localStorage:', error)
    }
  },

  clear: () => {
    try {
      // Remove only our keys
      const keysToRemove = []
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i)
        if (key && key.startsWith(STORAGE_KEY_PREFIX)) {
          keysToRemove.push(key)
        }
      }
      keysToRemove.forEach(key => localStorage.removeItem(key))
    } catch (error) {
      console.warn('Failed to clear localStorage:', error)
    }
  }
}

/**
 * sessionStorage adapter
 */
export const sessionStorageAdapter: StorageAdapter = {
  save: (key: string, data: unknown) => {
    try {
      sessionStorage.setItem(key, JSON.stringify(data))
    } catch (error) {
      console.warn('Failed to save to sessionStorage:', error)
    }
  },

  load: (key: string) => {
    try {
      const stored = sessionStorage.getItem(key)
      return stored ? JSON.parse(stored) : null
    } catch (error) {
      console.warn('Failed to load from sessionStorage:', error)
      return null
    }
  },

  remove: (key: string) => {
    try {
      sessionStorage.removeItem(key)
    } catch (error) {
      console.warn('Failed to remove from sessionStorage:', error)
    }
  },

  clear: () => {
    try {
      // Remove only our keys
      const keysToRemove = []
      for (let i = 0; i < sessionStorage.length; i++) {
        const key = sessionStorage.key(i)
        if (key && key.startsWith(STORAGE_KEY_PREFIX)) {
          keysToRemove.push(key)
        }
      }
      keysToRemove.forEach(key => sessionStorage.removeItem(key))
    } catch (error) {
      console.warn('Failed to clear sessionStorage:', error)
    }
  }
}

/**
 * Memory adapter (for testing or when storage is not available)
 */
const memoryStorage = new Map<string, unknown>()

export const memoryStorageAdapter: StorageAdapter = {
  save: (key: string, data: unknown) => {
    memoryStorage.set(key, data)
  },

  load: (key: string) => {
    return memoryStorage.get(key) || null
  },

  remove: (key: string) => {
    memoryStorage.delete(key)
  },

  clear: () => {
    // Remove only our keys
    for (const key of memoryStorage.keys()) {
      if (key.startsWith(STORAGE_KEY_PREFIX)) {
        memoryStorage.delete(key)
      }
    }
  }
}

/**
 * Get appropriate storage adapter
 * @param type Storage type preference
 * @returns Storage adapter
 */
export function getStorageAdapter(type: 'localStorage' | 'sessionStorage' | 'memory'): StorageAdapter {
  switch (type) {
    case 'localStorage':
      return typeof localStorage !== 'undefined' ? localStorageAdapter : memoryStorageAdapter
    case 'sessionStorage':
      return typeof sessionStorage !== 'undefined' ? sessionStorageAdapter : memoryStorageAdapter
    case 'memory':
    default:
      return memoryStorageAdapter
  }
}

/**
 * Generate storage key
 * @param gridId Grid identifier
 * @returns Full storage key
 */
export function getStorageKey(gridId: string): string {
  return `${STORAGE_KEY_PREFIX}${gridId}`
}

/**
 * Save grid state
 * @param gridId Grid identifier
 * @param state Grid state to save
 * @param adapter Storage adapter to use
 */
export function saveGridState(
  gridId: string,
  state: GridState,
  adapter: StorageAdapter = localStorageAdapter
): void {
  const key = getStorageKey(gridId)

  // Only save the parts we need to persist
  const persistableState = {
    blocks: state.blocks,
    activeMode: state.activeMode,
    // Don't persist viewport or transient state like activeDivider
  }

  adapter.save(key, persistableState)
}

/**
 * Load grid state
 * @param gridId Grid identifier
 * @param adapter Storage adapter to use
 * @returns Loaded grid state or null
 */
export function loadGridState(
  gridId: string,
  adapter: StorageAdapter = localStorageAdapter
): Partial<GridState> | null {
  const key = getStorageKey(gridId)
  return adapter.load(key) as Partial<GridState> | null
}

/**
 * Remove grid state
 * @param gridId Grid identifier
 * @param adapter Storage adapter to use
 */
export function removeGridState(
  gridId: string,
  adapter: StorageAdapter = localStorageAdapter
): void {
  const key = getStorageKey(gridId)
  adapter.remove(key)
}

/**
 * Get all saved grid states
 * @param adapter Storage adapter to use
 * @returns Map of grid ID to state
 */
export function getAllGridStates(
  adapter: StorageAdapter = localStorageAdapter
): Record<string, Partial<GridState>> {
  const states: Record<string, Partial<GridState>> = {}

  try {
    // This is a bit hacky but works for localStorage/sessionStorage
    if (adapter === localStorageAdapter && typeof localStorage !== 'undefined') {
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i)
        if (key && key.startsWith(STORAGE_KEY_PREFIX)) {
          const gridId = key.substring(STORAGE_KEY_PREFIX.length)
          const state = adapter.load(key)
          if (state) {
            states[gridId] = state
          }
        }
      }
    } else if (adapter === sessionStorageAdapter && typeof sessionStorage !== 'undefined') {
      for (let i = 0; i < sessionStorage.length; i++) {
        const key = sessionStorage.key(i)
        if (key && key.startsWith(STORAGE_KEY_PREFIX)) {
          const gridId = key.substring(STORAGE_KEY_PREFIX.length)
          const state = adapter.load(key)
          if (state) {
            states[gridId] = state
          }
        }
      }
    } else if (adapter === memoryStorageAdapter) {
      for (const key of memoryStorage.keys()) {
        if (key.startsWith(STORAGE_KEY_PREFIX)) {
          const gridId = key.substring(STORAGE_KEY_PREFIX.length)
          const state = adapter.load(key)
          if (state) {
            states[gridId] = state
          }
        }
      }
    }
  } catch (error) {
    console.warn('Failed to get all grid states:', error)
  }

  return states
}

/**
 * Create a custom storage adapter from a function
 * @param saveState Function to save state
 * @returns Custom storage adapter
 */
export function createCustomAdapter(saveState: (state: GridState) => void): StorageAdapter {
  return {
    save: (_key: string, data: unknown) => saveState(data as GridState),
    load: () => null, // Custom adapters typically don't load
    remove: () => {},
    clear: () => {}
  }
}

