export interface BlockConfig {
  id: string
  type: BlockType
  direction?: Direction

  // Size configuration
  defaultSize?: number
  minSize?: number
  maxSize?: number
  sizeUnit?: SizeUnit

  // Runtime size tracking
  size?: number
  originalDefaultSize?: number

  // Collapse behavior
  collapsible?: boolean
  collapseAt?: number
  collapseTo?: number

  // Divider configuration
  dividerPosition?: DividerPosition
  dividerSize?: number

  // Hierarchy
  parentId?: string
  order?: number
  children?: string[]
}

